function cleanString(input) {
    const parts = input.match(/.{1,16}/g);
    let result = "";
    for (let i = 0; i < parts.length; i++) {
      let part = parts[i];
      for (let j = 0; j < part.length; j++) {
        if (j === 1 || j === 3 || j === 5 || j === 6 || j === 7 || j === 12 || j === 14 || j === 15) {
          result += part[j];
        }
      }
    }  
    return result;
 }
  
function sortString(input) {
    const parts = input.match(/.{1,8}/g);
    
    let result = '';
  
    const order = [0, 3, 4, 5, 6, 2, 7, 1];
  
    for (let part of parts) {
      let formattedPart = "";
      for (let index of order) {
        if (index < part.length) {
          formattedPart += part[index];
        } 
      }
      result += formattedPart;
    }
  
    return result
}
